// items[0].json √ºzerinden 'value' dizisine ula≈üƒ±yoruz
const foldersData = foldersInput.json.value || [];
const folderMap = {};

for (const folder of foldersData) {
    // Klas√∂r Adƒ±: Klas√∂r ID ≈üeklinde haritalƒ±yoruz
    folderMap[folder.displayName] = folder.id;
}

const results = [];

// 4. Mailleri ƒ∞≈üleme
for (const emailItem of emailsToProcess) {
    const emailJson = emailItem.json;

    // HATA D√úZELTMESƒ∞: .output kƒ±smƒ±nƒ± kaldƒ±rdƒ±k √ß√ºnk√º veri doƒürudan k√∂kte.
    // Eƒüer label yoksa bu adƒ±mƒ± atla
    if (!emailJson.label) {
