    }

    const label = emailJson.label;
    // √ñzel karakterli key'i k√∂≈üeli parantez ile alƒ±yoruz
    const accessToken = emailJson["input1.json->access_token"];
    // HATA D√úZELTMESƒ∞: .output kaldƒ±rƒ±ldƒ±
    const mailId = emailJson.mail_id;

    let folderId = null;

    // --- E≈ûLE≈ûTƒ∞RME MANTIƒûI ---

    // 1. Tam E≈üle≈üme (Label direkt klas√∂r adƒ±ysa)
    if (folderMap[label]) {
        folderId = folderMap[label];
    } 
    // 2. Urgent / Important Varyasyonlarƒ± (AI bazen kelimelerin yerini deƒüi≈ütirebilir)
    else if (label === "Urgent_Important" || label === "Important_Urgent") {
        folderId = folderMap["Urgent_Important"];
    }
    else if (label === "Urgent_NotImportant" || label === "NotImportant_Urgent") {
        folderId = folderMap["Urgent_NotImportant"];
    }
    else if (label === "NotUrgent_Important" || label === "Important_NotUrgent") {
        folderId = folderMap["NotUrgent_Important"];
    }
    else if (label === "NotUrgent_NotImportant" || label === "NotImportant_NotUrgent") {
        folderId = folderMap["NotUrgent_NotImportant"];
    }

    // 5. Sonu√ß Objesini Olu≈üturma
